import {HTTP} from '@/services/axios.js'

const newPost = function () {
  return {
    online: false,
    liked:0,
    user: {
      name: 'Planck Anders',
      image_url: 'https://scontent-mxp1-1.xx.fbcdn.net/v/t1.6435-9/69245733_725030284603206_5683149216247971840_n.jpg?_nc_cat=104&ccb=1-5&_nc_sid=09cbfe&_nc_ohc=2wstdYB3PNQAX89jAUm&tn=L8SKpZX-hIdGHkiX&_nc_ht=scontent-mxp1-1.xx&oh=9067fd6a9e1a4aa9a72add11944ddd40&oe=614F37FD',
    },
    title: '',
    description: '',
    images: [],
    tags:[],
  }
}


const registerPost = async function (data) {
  let accessInfo = localStorage.getItem('accessInfo')
  if (accessInfo != 'undefined' && accessInfo != null) {
    accessInfo = JSON.parse(accessInfo)
    let formData = new FormData()

    Object.entries(data).forEach(
      ([key, value]) => formData.append(key, value)
    )

    console.log(formData,data)
    await HTTP.post('posts', data, {
        headers: {
          'Authorization': 'Bearer ' + accessInfo.token,
           'Content-Type': "application",
        }
      })
      .then(response => {
        console.log(response.data)
      })
      .catch(error => {
        console.log(error)
      })
  }
}

const GetAllPosts = async function () {
  let user = localStorage.getItem('accessInfo')

  if (user!='undefined' && user!=null) {
    user=JSON.parse(user)
    await HTTP.get('posts',{
      headers:{
        'Authorization': 'Bearer ' + user.token,
      }
    })
    .then( response => {
      localStorage.setItem('Posts',JSON.stringify(response.data))
    })
    .catch(error => {
      console.log(error)
    })
  }

}

const allPosts = function(){
  GetAllPosts()
  let posts= localStorage.getItem('Posts')
  if(posts!='undefined' && posts!=null) {
    return JSON.parse(posts)
  }
  return []
}

export {
  newPost,
  allPosts,
  registerPost,
}
