import {
    newPost,
    allPosts,
    registerPost,
} from '@/services/PostsServices.js'
import router from '../../router'



export default {
    state: {
        post: newPost(),
        posts: allPosts(),
        step: 1,
    },

    mutations: {
        nextStep: state => state.step++,
        backStep: state => state.step--,
        setStep: (state, payload) => (state.step = payload),
        addPost: async (state, payload) => {
            //state.posts.push(payload)
            registerPost(payload)
            state.posts= await allPosts()
            router.push('/')
        },
        setCommentPost: (state, payload) => (state.posts[payload.id].comments.push(payload)),
        resetPost: state => state.post = newPost(),
    },
    actions: {},
    getters: {
        getPost: state => state.post,
        getPosts: state => state.posts.sort((a, b) => b.id - a.id),
        getStep: state => state.step,
    },
}
