import {
    newPost,
    allPosts,
    registerPost,
} from '@/services/PostsServices.js'
import router from '../../router'
import {
    HTTP
} from '../../services/axios'



export default {
    state: {
        post: newPost(),
        posts: [],
        step: 1,
    },

    mutations: {
        nextStep: state => state.step++,
        backStep: state => state.step--,
        RESET_post: state => state.post = newPost(),
        setStep: (state, payload) => state.step = payload,

        ADD_Post: (state, payload) => {
            state.posts.push(payload)
            router.push('/')

        },
        ADD_Tags_Post: (state, payload) => {

        },

        ADD_Images_Post: (state, payload) => {

        },

        ADD_Comment_Post: async (state, payload) => {
            let user = localStorage.getItem('accessInfo')

            if (user != 'undefined' && user != null) {
                user = JSON.parse(user)
                let formData = new FormData()
                Object.entries(payload).forEach(
                    ([key, value]) => formData.append(key, value)
                )
                await HTTP.post('posts/' + payload.post_id + '/comments', formData, {
                        headers: {
                            'Authorization': 'Bearer ' + user.token,
                            'Content-Type': "multipart/form-data",
                        }
                    })
                    .then(response => {
                        allPosts()
                    })
                    .catch(error => console.log(error))
            }
        },

        SET_Posts: (state, payload) => {
            state.posts = payload
            console.log(state.posts)
        },
    },
    actions: {
        resetPost({commit}) {
            commit('RESET_post')
        },
        nextStep({commit}) {
            commit('nextStep')
        },
        backStep({commit}) {
            commit('backStep')
        },
        setStep({commit}) {
            commit('setStep')
        },

        addPost({commit}) {
            let accessInfo = localStorage.getItem('accessInfo')
            if (accessInfo != 'undefined' && accessInfo != null) {
                accessInfo = JSON.parse(accessInfo)
                let formData = new FormData()
                Object.entries(data.Post).forEach(
                    ([key, value]) => formData.append(key, value)
                )
                HTTP.post('posts', formData, {
                        headers: {
                            'Authorization': 'Bearer ' + accessInfo.token,
                            'Content-Type': "multipart/form-data",
                        }
                    })
                    .then(response => {

                        commit('ADD_Post', response.data)
                        if (data.tags.length > 0) {
                            data.tags.forEach(tag => {
                                formData = new FormData()
                                Object.entries(tag).forEach(
                                    ([key, value]) => formData.append(key, value)
                                )
                                HTTP.post('posts/' + response.data.id + '/tags', formData, {
                                        headers: {
                                            'Authorization': 'Bearer ' + accessInfo.token,
                                            'Content-Type': "multipart/form-data",
                                        }
                                    })
                                    .then(response => {
                                        commit('ADD_Tags_Post', response.data)
                                    })
                                    .catch(error => {
                                        console.log(error)
                                    })
                            })
                        }

                        if (data.images.length > 0) {
                            data.images.forEach(image => {
                                formData = new FormData()
                                const req = {
                                    image: image,
                                    size: image.size,
                                    name: image.name
                                }
                                Object.entries(req).forEach(
                                    ([key, value]) => formData.append(key, value)
                                )
                                HTTP.post('posts/' + response.data.id + '/images', formData, {
                                        headers: {
                                            'Authorization': 'Bearer ' + accessInfo.token,
                                            'Content-Type': "multipart/form-data",
                                        }
                                    })
                                    .then(response => {
                                        commit('ADD_Images_Post', response.data)
                                    })
                                    .catch(error => {
                                        console.log(error)
                                    })
                            })
                        }
                    })
                    .catch(error => {
                        console.log(error)
                    })

                GetAllPosts()
                return
            }
        },
        getPosts({
            commit
        }) {
            let accessInfo = localStorage.getItem('accessInfo')

            if (accessInfo != 'undefined' && accessInfo != null) {
                accessInfo = JSON.parse(accessInfo)
                HTTP.get('posts', {
                        headers: {
                            'Authorization': 'Bearer ' + accessInfo.token,
                        }
                    })
                    .then(response => {
                        commit('SET_Posts', response.data)
                    })
                    .catch(error => {
                        console.log(error)
                    })
            }
        },



    },
    getters: {
        getPost: state => state.post,
        getPosts: state => state.posts.sort((a, b) => b.id - a.id),
        getStep: state => state.step,
    },
}
