import {
    newPost,
    allPosts,
    registerPost,
} from '@/services/PostsServices.js'
import router from '../../router'
import {
    HTTP
} from '../../services/axios'



export default {
    state: {
        post: newPost(),
        posts: [],
        step: 1,
    },

    mutations: {
        nextStep: state => state.step++,
        backStep: state => state.step--,
        RESET_post: state => state.post = newPost(),
        setStep: (state, payload) => (state.step = payload),
        addPost: (state, payload) => {
            //state.posts.push(payload)
            registerPost(payload)

            state.posts = allPosts
            router.push('/')

        },
        setCommentPost: async (state, payload) => {
            let user = localStorage.getItem('accessInfo')

            if (user != 'undefined' && user != null) {
                user = JSON.parse(user)
                let formData = new FormData()
                Object.entries(payload).forEach(
                    ([key, value]) => formData.append(key, value)
                )
                await HTTP.post('posts/' + payload.post_id + '/comments', formData, {
                        headers: {
                            'Authorization': 'Bearer ' + user.token,
                            'Content-Type': "multipart/form-data",
                        }
                    })
                    .then(response => {
                        allPosts()
                    })
                    .catch(error => console.log(error))
            }
        },
        SET_Posts: (state,payload) =>{
            state.posts=payload
            console.log(state.posts)
        },
    },
    actions: {
        getPosts({commit}) {
            let accessInfo = localStorage.getItem('accessInfo')

            if (accessInfo != 'undefined' && accessInfo != null) {
                accessInfo = JSON.parse(accessInfo)
                HTTP.get('posts', {
                        headers: {
                            'Authorization': 'Bearer ' + accessInfo.token,
                        }
                    })
                    .then(response => {
                        commit('SET_Posts', response.data)
                    })
                    .catch(error => {
                        console.log(error)
                    })
            }
        },
        resetPost({commit}){
            commit('RESET_post')
        }

    },
    getters: {
        getPost: state => state.post,
        getPosts: state => state.posts.sort((a, b) => b.id - a.id),
        getStep: state => state.step,
    },
}
