import {HTTP} from '@/services/axios.js'

const newPost = function () {
  return {
    id: null,
    online: false,
    at: "",
    liked: {
      state: false,
      nber: 0,
    },
    author: {
      name: 'Planck Anders',
      foto: 'https://scontent-mxp1-1.xx.fbcdn.net/v/t1.6435-9/69245733_725030284603206_5683149216247971840_n.jpg?_nc_cat=104&ccb=1-5&_nc_sid=09cbfe&_nc_ohc=2wstdYB3PNQAX89jAUm&tn=L8SKpZX-hIdGHkiX&_nc_ht=scontent-mxp1-1.xx&oh=9067fd6a9e1a4aa9a72add11944ddd40&oe=614F37FD',
    },
    tags: [],
    title: '',
    description: '',
    images: [],
    comments: [],
  }
}


const GetAllPosts = async function () {
  let user = localStorage.getItem('accessInfo')
  let res;
  if (user!='undefine' && user!=null) {
    user=JSON.parse(user)
    await HTTP.get('posts',{
      headers:{
        'Authorization': 'Bearer ' + user.token
      }
    })
    .then( response => {
      console.log(response.data)
      localStorage.setItem('Posts',JSON.stringify(response.data))
    })
    .catch(error => {
      console.log(error)
    })
  }

}

const allPosts = function(){
  GetAllPosts()
  let posts= localStorage.getItem('Posts')
  if(posts!='undefined' && posts!=null) return JSON.parse(posts)
  else return []
}

export {
  newPost,
  allPosts
}
